sys_variable_value.value:
(function execute(inputs, outputs) {
  var json = new global.JSON();

  var field = inputs.field;
  var values = json.decode(inputs.inputValues);

  var dt = new sn_dt.DecisionTableAPI();
  var dtInputs = new Object();
  for (i in values) {
    dtInputs[i] = values[i]+'';
  }  
  var response = dt.getDecision(inputs.decisionTable, dtInputs);

  if (field) {
    outputs.answer = response.getValue(field);
    outputs.answer_display = response.getDisplayValue(field);
  }  else {
    outputs.answer = response.sys_id;
    outputs.answer_display = response.getDisplayValue();
  } 
})(inputs, outputs);

