sys_variable_value.value:
(function execute(inputs, outputs) {
 var plBuilder = new StagePayloadBuilder();
  var payload = plBuilder.createPayload(inputs.record);
  payload.u_api_key = inputs.api_key+'';

  var ep = new GlideRecord('x_snc_con_endpoint');
  ep.get('api_key', inputs.api_key+'');

  var stage = new GlideRecord('x_snc_con_stage');
  stage.name = inputs.record.getDisplayValue();
  stage.state = 'Ready';
  stage.endpoint = ep.sys_id + '';
  stage.table = inputs.record.getTableName();
  stage.payload = JSON.stringify(payload);
  var stageID = stage.insert();
  outputs.stage = stageID;
  outputs.stage_id = stageID + '';
  outputs.credential_alias = stage.endpoint.credential_alias+'';
  outputs.endpoint = stage.endpoint.endpoint+'';
  outputs.table = stage.table +'';

})(inputs, outputs);

