sys_variable_value.value:
(function execute(inputs, outputs) {
  var responseBody = JSON.parse(inputs.response);

  var importState = responseBody.result.import_state +'';
  var importError = responseBody.result.import_error +'';
  var activity = 'Import State: ' + importState + '\n Import Message: ' + importError;

  if(inputs.status!=201 && inputs.status!=200){
    outputs.state = 'Error';
   	//var errorMsg =  responseBody.error.message;
    //var errorDetail = responseBody.error.detail;
    //throw "Error creating. Message: "+errorMsg + " Details:"+errorDetail;
  } else {
    outputs.state = 'Complete';

  }

  outputs.activity = activity;

})(inputs, outputs);

