sys_variable_value.value:
(function execute(inputs, outputs) {
  var responseBody = JSON.parse(inputs.response);
  var payload = JSON.parse(responseBody.result.payload);
  responseBody.result.payload = payload;
  //gs.info('responseBody: ' + JSON.stringify(responseBody));
  var xml = json2xml(responseBody);
  //gs.info('xml: ' + JSON.stringify(xml));
  var table = responseBody.result.table;
  var name = responseBody.result.name;
  var state = responseBody.result.state;

  outputs.payload = xml;
  outputs.table = table;
  outputs.name = name;
  outputs.state = state;

  if(inputs.status!=200 && inputs.status!=201){
   	//var errorMsg =  responseBody.error.message;
    //var errorDetail = responseBody.error.detail;
    //throw "Error creating incident. Message: "+errorMsg + " Details:"+errorDetail;
  }

   /*	This work is licensed under Creative Commons GNU LGPL License.

	License: http://creativecommons.org/licenses/LGPL/2.1/
   Version: 0.9
	Author:  Stefan Goessner/2006
	Web:     http://goessner.net/ 
*/
function json2xml(o, tab) {
   var toXml = function(v, name, ind) {
      var xml = "";
      if (v instanceof Array) {
         for (var i=0, n=v.length; i<n; i++)
            xml += ind + toXml(v[i], name, ind+"\t") + "\n";
      }
      else if (typeof(v) == "object") {
         var hasChild = false;
         xml += ind + "<" + name;
         for (var m in v) {
            if (m.charAt(0) == "@")
               xml += " " + m.substr(1) + "=\"" + v[m].toString() + "\"";
            else
               hasChild = true;
         }
         xml += hasChild ? ">" : "/>";
         if (hasChild) {
            for (var m in v) {
               if (m == "#text")
                  xml += v[m];
               else if (m == "#cdata")
                  xml += "<![CDATA[" + v[m] + "]]>";
               else if (m.charAt(0) != "@")
                  xml += toXml(v[m], m, ind+"\t");
            }
            xml += (xml.charAt(xml.length-1)=="\n"?ind:"") + "</" + name + ">";
         }
      }
      else {
         xml += ind + "<" + name + ">" + v.toString() +  "</" + name + ">";
      }
      return xml;
   }, xml="";
   for (var m in o)
      xml += toXml(o[m], m, "");
   return tab ? xml.replace(/\t/g, tab) : xml.replace(/\t|\n/g, "");
}

})(inputs, outputs);

