sys_variable_value.value:
(function execute(inputs, outputs) {

    var answer = '';
        var ep = new GlideRecord('x_snc_con_endpoint');
        ep.addActiveQuery();
        ep.addQuery('api_key', inputs.api_key);
        ep.query();
        if (ep.next()) {
            answer = ep;
        }

  outputs.endpoint = answer;
  outputs.endpoint_url = ep.endpoint + '';
  outputs.credential_alias = ep.credential_alias + '';

})(inputs, outputs);

